const stopwords = new Set([
  'a', 'an', 'and', 'are', 'as', 'at', 'be', 'by', 'for', 'from', 'has', 'he',
  'in', 'is', 'it', 'its', 'of', 'on', 'or', 'that', 'the', 'this', 'to', 'was',
  'were', 'will', 'with', 'the', 'their', 'they', 'them', 'these', 'those',
  'am', 'been', 'being', 'have', 'had', 'having', 'do', 'does', 'did', 'doing',
  'would', 'should', 'could', 'shall', 'should', 'may', 'might', 'must'
]);

const synonymDictionary = {
  'develop': ['create', 'build', 'design', 'implement'],
  'create': ['develop', 'build', 'design', 'implement'],
  'build': ['develop', 'create', 'design', 'implement'],
  'design': ['develop', 'create', 'build', 'implement'],
  'implement': ['develop', 'create', 'build', 'design'],
  'manage': ['lead', 'direct', 'oversee', 'supervise'],
  'lead': ['manage', 'direct', 'oversee', 'supervise'],
  'direct': ['manage', 'lead', 'oversee', 'supervise'],
  'oversee': ['manage', 'lead', 'direct', 'supervise'],
  'supervise': ['manage', 'lead', 'direct', 'oversee'],
  'analyze': ['examine', 'evaluate', 'assess', 'review'],
  'examine': ['analyze', 'evaluate', 'assess', 'review'],
  'evaluate': ['analyze', 'examine', 'assess', 'review'],
  'assess': ['analyze', 'examine', 'evaluate', 'review'],
  'review': ['analyze', 'examine', 'evaluate', 'assess']
};

export const processText = (text) => {
  // Convert to lowercase and split into words
  const words = text.toLowerCase()
    .replace(/[^\w\s]/g, '') // Remove punctuation
    .split(/\s+/); // Split on whitespace

  // Filter out stopwords and short words
  const keywords = words.filter(word => 
    word.length > 2 && 
    !stopwords.has(word)
  );

  // Remove duplicates
  return [...new Set(keywords)];
};

export const findSynonyms = (word) => {
  return synonymDictionary[word.toLowerCase()] || [];
};

export const optimizeText = (text, targetKeywords) => {
  const sentences = text.split(/[.!?]+/).filter(s => s.trim());
  const optimizedSentences = [];
  let totalWordCount = 0;
  const originalWordCount = text.split(/\s+/).length;
  const maxWordIncrease = 30;

  for (const sentence of sentences) {
    const words = sentence.trim().split(/\s+/);
    const optimizedWords = [...words];
    let modified = false;

    // Check each word in the sentence
    for (let i = 0; i < words.length; i++) {
      const word = words[i].toLowerCase();

      // If the word is in target keywords, try to find a better synonym
      if (targetKeywords.includes(word)) {
        const synonyms = findSynonyms(word);
        if (synonyms.length > 0) {
          // Choose a synonym that better matches the job description
          const bestSynonym = synonyms[0];
          optimizedWords[i] = bestSynonym;
          modified = true;
        }
      }
    }

    // Only include modified sentences if we haven't exceeded the word limit
    const newWordCount = optimizedWords.join(' ').split(/\s+/).length;
    if (modified && (totalWordCount + newWordCount - words.length) <= originalWordCount + maxWordIncrease) {
      optimizedSentences.push(optimizedWords.join(' '));
      totalWordCount += newWordCount - words.length;
    } else {
      optimizedSentences.push(sentence.trim());
    }
  }

  return optimizedSentences.join('. ');
};
